import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Row.css";
import price from "../../images/Dashboard/Row/Group 4/Price.png";
import report from "../../images/Dashboard/Row/Group 2/statistics-report.png";
import csv from "../../images/Dashboard/Row/Group 3/file.png";
import calendar from "../../images/Dashboard/Row/Group/calendar.png";

const Row = () => {
  let [data, setData] = useState([]);

  useEffect(() => {
    const getcampaignlist = async () => {
      await axios({
        method: "get",
        url: "http://localhost:8000/"
      }).then(
        response => {
          console.log(response.data.data);
          setData(response.data.data);
        },
        error => {
          console.error(error);
        }
      );
    };
    getcampaignlist();
  }, []);
  return (
    <>
      {data.map((v, k) => {
        return (
          <tr>
            <td>{v.createdOn}</td>
            <td>
              <div className="card no-border">
                <div className="row no-gutters">
                  <div className="col-md-4">
                    <img
                      src={v.image_url}
                      className="card-img"
                      alt="campaign"
                    />
                  </div>
                  <div className="col-md-8">
                    <div className="card-body">
                      <h6 className="card-title">{v.name}</h6>
                      <p className="card-text">
                        <small className="text-muted">{v.region}</small>
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </td>
            <td>
              <img src={price} alt="price" className="action-images" />
              <a
                href="#pricing"
                data-toggle="modal"
                data-target="#pricingModal"
              >
                View Pricing
              </a>
            </td>
            <td>
              <div className="col-md-12">
                <div className="row">
                  <div className="col-md-3 margin-bottom-mobile">
                    <img src={csv} alt="csv" className="action-images" />
                    <a href="#csv">CSV</a>
                  </div>
                  <div className="col-md-4 margin-bottom-mobile">
                    <img src={report} alt="report" className="action-images" />
                    <a href="#report">Report</a>
                  </div>
                  <div className="col-md-5 margin-bottom-mobile">
                    <img
                      src={calendar}
                      alt="calendar"
                      className="action-images"
                    />
                    <a href="#pricing">Schedule again</a>
                  </div>
                </div>
              </div>
            </td>
            <div className="modal" id="pricingModal">
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-body">
                    <h4>Pricing</h4>
                    <div className="row">
                      <div className="col-md-6">1 week- 1 Month</div>
                      <div className="col-md-6 text-right price-bold">$100</div>
                    </div>
                  </div>

                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn close-btn"
                      data-dismiss="modal"
                    >
                      Close
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </tr>
        );
      })}
    </>
  );
};

export default Row;
